import React from 'react';
import AliceCarousel from 'react-alice-carousel';
import 'react-alice-carousel/lib/alice-carousel.css';
import { MainCarouselData } from './MainCarouselData';
import './MainCarousel.css'; // Import a CSS file for custom styles

const MainCarousel = () => {
  const items = MainCarouselData.map((item, index) => (
    <img
      key={index}
      className="carousel-image"
      role="presentation"
      src={item.image}
      alt={`Slide ${index + 1}`}
    />
  ));

  return (
    <AliceCarousel
      items={items}
      autoPlay
      autoPlayInterval={3000}
      infinite
      disableButtonsControls
      responsive={{
        0: { items: 1 }, // For smaller devices, show 1 item
        600: { items: 1 }, // For medium devices, show 2 items
        1024: { items: 3 }, // For larger devices, show 3 items
      }}
    />
  );
};

export default MainCarousel;
