import React, { useRef } from 'react';
import AliceCarousel from 'react-alice-carousel';
import HomeSectionCard from '../HomeSectionCard/HomeSectionCard';
import { Button } from "@mui/material";
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';

const HomeSectionCarousel = ({ data }) => {
  const carouselRef = useRef(null);

  const responsive = {
    0: { items: 1 },
    720: { items: 2 },
    1024: { items: 4.5 },
    2048: { items: 6 },
  };

  const items = data.slice(0, 10).map((product, index) => (
    <div
      key={index}
      className="transform transition-transform duration-500 ease-in-out hover:scale-105"
    >
      <HomeSectionCard product={product} />
    </div>
  ));

  const slidePrev = () => {
    if (carouselRef.current) {
      carouselRef.current.slidePrev();
    }
  };

  const slideNext = () => {
    if (carouselRef.current) {
      carouselRef.current.slideNext();
    }
  };

  return (
    <div className="border-hidden">
      <div className="relative p-5">
        <AliceCarousel
          items={items}
          disableDotsControls
          responsive={responsive}
          mouseTracking
          touchTracking
          animationDuration={800}
          animationType="fadeout"
          ref={carouselRef} // Reference to control navigation
        />
        <Button
          variant="contained"
          className="z-50"
          sx={{
            position: 'absolute',
            top: '50%',
            left: '-20px',
            transform: 'translateY(-50%)',
            color: 'white',
            bgcolor: 'gray',
            '&:hover': { bgcolor: 'darkgray' },
          }}
          aria-label="previous"
          onClick={slidePrev}
        >
          <KeyboardArrowLeftIcon />
        </Button>
        <Button
          variant="contained"
          className="z-50"
          sx={{
            position: 'absolute',
            top: '50%',
            right: '-20px',
            transform: 'translateY(-50%)',
            color: 'white',
            bgcolor: 'gray',
            '&:hover': { bgcolor: 'darkgray' },
          }}
          aria-label="next"
          onClick={slideNext}
        >
          <KeyboardArrowLeftIcon sx={{ transform: 'rotate(180deg)' }} />
        </Button>
      </div>
    </div>
  );
};

export default HomeSectionCarousel;
