import React from 'react'
import AliceCarousel from 'react-alice-carousel';
import HomeSectionCard from '../HomeSectionCard/HomeSectionCard';
import { Button } from "@mui/material";
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';

const HomeSectionCarousel = () => {
    const responsive = {
      0: { items: 1 },
      720: { items: 2 },
      1024: { items: 4.5 },
      2048: { items: 6 },
    };
  
    const items = [1, 1, 1, 1, 1].map((_, index) =>(
        <div key={index} className="transform transition-transform duration-500 ease-in-out hover:scale-105">
          <HomeSectonCard />
        </div>
      ));
    return (
      <div className="relative px-4 lg:px-8">
        <div className="relative p-5">
          <AliceCarousel
            items={items}
            disableButtonsControls
            responsive={responsive}
            infinite
            mouseTracking
            animationDuration={800}
            animationType="fadeout"
          />
          {/* Next Button */}
          <Button variant="contained" className="z-50"sx={{ position: 'absolute', top: '50%', right: '-20px', transform: 'translateY(-50%)', color: 'white', bgcolor: 'gray', '&:hover': { bgcolor: 'darkgray' },}} aria-label="next">
            <KeyboardArrowLeftIcon sx={{ transform: 'rotate(180deg)' }} />
          </Button>
        </div>
      </div>
    );
  };

export default HomeSectionCarousel;
