import React from 'react'
import AliceCarousel from 'react-alice-carousel';
import HomeSectonCard from '../HomeSectionCard/HomeSectionCard';
import { Button } from "@mui/material";
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';

const HomeSectonCarousel = () => {
    const resposnive ={
      0:{item:1},
      720:{item:2},  
      1024:{item:4.5},  
      2048:{item:6},  
    };

    const items=[1, 1, 1, 1, 1].map((item)=><HomeSectonCard/>);

    return (
        <div className='relative px-4 lg:px-8'>
        <div className='relative p-5'>
            <AliceCarousel
                  items={items}
                  disableButtonsControls
                  infinite
                  responsive={resposnive}
                />
            <Button variant="contained" className="z-50" sx={{position:'absolute', top:"8rem", right:"0rem", transform:"translateX(50%) rotate(180deg)", color:"green", bgcolor:"grey"}} aria-label='next'> <KeyboardArrowLeftIcon /> </Button>
        </div>
    </div>
    )
}

export default HomeSectonCarousel;
