import React, { useState } from 'react';
import AliceCarousel from 'react-alice-carousel';
import HomeSectionCard from '../HomeSectionCard/HomeSectionCard';
import { Button } from '@mui/material';
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';

const HomeSectionCarousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);

  const items = [1, 1, 1, 1, 1, 1, 1]; // Carousel items

  const responsive = {
    0: { items: 1 },
    720: { items: 2 },
    1024: { items: 4.5 },
    2048: { items: 6 },
  };

  const totalItems = items.length;

  const slidePrev = () => {
    if (activeIndex > 0) {
      setActiveIndex(activeIndex - 1);
    }
  };

  const slideNext = () => {
    if (activeIndex < totalItems - 1) {
      setActiveIndex(activeIndex + 1);
    }
  };

  const syncActiveIndex = ({ item }) => setActiveIndex(item);

  const carouselItems = items.map((_, index) => (
    <div
      key={index}
      className="transform transition-transform duration-500 ease-in-out hover:scale-105"
    >
      <HomeSectionCard />
    </div>
  ));

  return (
    <div className="border-hidden">
      <div className="relative p-5">
        <AliceCarousel
          items={carouselItems}
          disableButtonsControls
          disableDotsControls
          responsive={responsive}
          infinite={false} // Disable infinite scrolling
          mouseTracking
          animationDuration={800}
          animationType="fadeout"
          onSlideChanged={syncActiveIndex}
          activeIndex={activeIndex}
        />
        {/* Next Button */}
        {activeIndex < totalItems - 1 && (
          <Button
            variant="contained"
            className="z-50"
            sx={{
              position: 'absolute',
              top: '50%',
              right: '-20px',
              transform: 'translateY(-50%)',
              color: 'white',
              bgcolor: 'gray',
              '&:hover': { bgcolor: 'darkgray' },
            }}
            aria-label="next"
            onClick={slideNext}
          >
            <KeyboardArrowLeftIcon sx={{ transform: 'rotate(180deg)' }} />
          </Button>
        )}
        {/* Previous Button */}
        {activeIndex > 0 && (
          <Button
            variant="contained"
            className="z-50"
            sx={{
              position: 'absolute',
              top: '50%',
              left: '-20px',
              transform: 'translateY(-50%)',
              color: 'white',
              bgcolor: 'gray',
              '&:hover': { bgcolor: 'darkgray' },
            }}
            aria-label="previous"
            onClick={slidePrev}
          >
            <KeyboardArrowLeftIcon />
          </Button>
        )}
      </div>
    </div>
  );
};

export default HomeSectionCarousel;
