import React from 'react'
import AliceCarousel from 'react-alice-carousel';
import HomeSectonCard from '../HomeSectionCard/HomeSectionCard';
import { Button } from "@mui/material";
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';

const HomeSectonCarousel = () => {
    const responsive = {
      0: { items: 1 },
      720: { items: 2 },
      1024: { items: 4.5 },
      2048: { items: 6 },
    };
  
    const items = [1, 1, 1, 1, 1].map((_, index) => <HomeSectonCard key={index} />);
  
    return (
      <div className="relative px-4 lg:px-8">
        <div className="relative p-5">
          <AliceCarousel
            items={items}
            disableButtonsControls
            responsive={responsive}
            infinite
            mouseTracking
            animationDuration={600}
          />
          {/* Next Button */}
          <Button variant="contained" className="z-50"sx={{ position: 'absolute', top: '50%', right: '-20px', transform: 'translateY(-50%)', color: 'white', bgcolor: 'gray', '&:hover': { bgcolor: 'darkgray' },}} aria-label="next">
            <KeyboardArrowLeftIcon sx={{ transform: 'rotate(180deg)' }} />
          </Button>
        </div>
      </div>
    );
  };

export default HomeSectonCarousel;
